mapper.select:
SELECT u.id, u.sex, u.id_iccid, u.contract_no, u.user_name, u.login_no, u.phone_no, u.register_time, d.dept_name AS dept_name, r.role_name, c.system_fee+c.user_fee AS total_fee, c.system_fee, c.user_fee FROM sys_users u LEFT JOIN sys_user_role ur ON u.id = ur.user_id LEFT JOIN sys_contract_info c ON u.contract_no = c.id LEFT JOIN sys_role r ON ur.role_id = r.id LEFT JOIN sys_dept d ON u.dept_id = d.id WHERE 1=1
AND u.role_id= #{roleId}
AND register_time > str_to_date(#{registerTime},'%Y-%m-%d %H:%i:%s')
AND u.phone_no=#{phoneNo}
